<div class="activity_like_holder comment_mini">{img theme='layout/like.png' class='v_middle'}&nbsp;{if $aFeed.feed_is_liked}{if !count($aFeed.likes) == 1}{phrase var='like.you'}{elseif count($aFeed.likes) == 1}{phrase var='like.you_and'}{else}{phrase var='like.you_comma'}{/if}{/if} {foreach from=$aFeed.likes name=likes item=aLikeRow}{$aLikeRow|user}{if $phpfox.iteration.likes == (count($aFeed.likes) - 1) && $aFeed.feed_total_like <= Phpfox::getParam('feed.total_likes_to_display')} {phrase var='like.and'} {elseif $phpfox.iteration.likes != count($aFeed.likes)}, {/if}{/foreach}{if $aFeed.feed_total_like > Phpfox::getParam('feed.total_likes_to_display')} {phrase var='like.and'} <a href="#" onclick="return $Core.box('like.browse', 400, 'type_id={$aFeed.like_type_id}&amp;item_id={$aFeed.item_id}');">{if $iTotalLeftShow = ($aFeed.feed_total_like - Phpfox::getParam('feed.total_likes_to_display'))}{/if} {if $iTotalLeftShow == 1}{phrase var='like.1_other_person'}{else}{$iTotalLeftShow|number_format} {phrase var='like.others'}{/if}</a> {phrase var='like.like_this'}{else}{if count($aFeed.likes) == 2} {phrase var='like.likes_this'}{else}{if $aFeed.feed_is_liked} {phrase var='like.like_this'}{else} {phrase var='like.likes_this'}{/if}{/if}{/if}</div>